mapper.update:
UPDATE STORE SET
classify=#{classify}, name= #{name}, address=#{address},
phone=#{phone},
menu=#{menu}, time={time}, reserving=#{reserving}, etc=#{etc}
WHERE pwd=#{pwd}

UPDATE STORE SET count =
count+1
WHERE name=#{name}

mapper.select:
select * from STORE where STORE_NUM IN
(select STORE_NUM from STORE_TAG where TAG_ID=(select TAG_ID from TAG where
TAG_NAME=#{keyword}));
